mapper.select:
select * from t_osms_homework_courseware
where student_id = #{studentId,jdbcType=VARCHAR}
and courseware_name = #{coursewareName,jdbcType=VARCHAR}
limit 1

mapper.update:
update t_osms_homework_courseware set is_done = #{isDone,jdbcType=INTEGER}
where student_id = #{studentId,jdbcType=VARCHAR}
and courseware_name = #{coursewareName,jdbcType=VARCHAR}
